import React, { Fragment, useEffect } from "react";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { getCurrentProfile, deleteAccount } from "../../actions/profile";
import Spinner from "../layout/Spinner";
import ProfileTop from "../profile/ProfileTop";
import DashboardActions from "./DashboardActions";
import Experience from "./Experience";
import Education from "./Education";

const Dashboard = ({
  getCurrentProfile,
  deleteAccount,
  auth: { user },
  profile: { profile, loading },
}) => {
  useEffect(() => {
    getCurrentProfile();
  }, [getCurrentProfile]);

  return loading && profile === null ? (
    <Spinner />
  ) : (
    <div className="dashboard-page-container">
      {/* HEADER */}
      <div className="dashboard-header">
        <h1 className="large text-primary">Profile</h1>
        <p className="lead">
          <i className="fas fa-user"></i> Welcome {user && user.name}
        </p>
        <DashboardActions className="actions-dash" />
      </div>

      {/* MAIN */}
      {profile !== null ? (
        <>
          <div className="dashboard-main">
            {/* 1 */}
            <div className="dashboard-main-top">
              {/* 1.1 */}
              <ProfileTop
                profile={profile}
                className="dashboard-main-top-profile-card"
              />
              {/* 1.2 */}
              <div className="dashboard-main-top-skills">SKILLS GO HERE</div>
            </div>

            {/* 2 */}
            <div className="dashboard-main-bottom">
              {/* 2.1 */}
              <Experience
                experience={profile.experience}
                className="exp-dash dashboard-main-bottom-exp"
              />
              {/* 2.2 */}
              <Education
                education={profile.education}
                className="edu-dash dashboard-main-bottom-edu"
              />
            </div>
          </div>
          {/* FOOTER */}
          <button
            className="btn btn-danger btn-delete-profile"
            onClick={() => deleteAccount()}
          >
            <i className="fas fa-user-minus"></i> Delete My Account
          </button>
        </>
      ) : (
        // 3
        <Fragment>
          <p>
            You have not filled up your profile yet, please add some information
          </p>
          <Link to="/create-profile" className="btn btn-primary my-1">
            Create Profile
          </Link>
        </Fragment>
      )}
    </div>
  );
};

Dashboard.propTypes = {
  getCurrentProfile: PropTypes.func.isRequired,
  // deleteAccount: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  profile: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
  profile: state.profile,
  deleteAccount: PropTypes.func.isRequired,
});

export default connect(mapStateToProps, { getCurrentProfile, deleteAccount })(
  Dashboard
);
