import React, { Fragment, useEffect } from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { logout } from "../../actions/auth";
import Dashboard from "../dashboard/Dashboard";

// {
//   getCurrentProfile,
//   deleteAccount,
//   auth: { user },
//   profile: { profile, loading },
// }

const NavbarAlternative = ({
  auth: {
    user,
    // user: { name },
    isAuthenticated,
    loading,
  },
  logout,
}) => {
  //
  let trimmedUserName = (username) => {
    return username.trim().split(" ")[0];
  };

  const [menuActive, setMenuState] = useState(false);

  // let searchBarExtends = () => {
  //   $(".nav").toggleClass("search");
  //   $(".nav").toggleClass("no-search");
  //   $(".search-input").toggleClass("search-active");
  // };

  // $("#search-icon").click(function () {
  //   $(".nav").toggleClass("search");
  //   $(".nav").toggleClass("no-search");
  //   $(".search-input").toggleClass("search-active");
  // });

  let menuToggleFunc = () => {
    $(".nav").toggleClass("mobile-nav");
    $(this).toggleClass("is-active");

    var css = this.props.showHideSidenav === "hidden" ? "show" : "hidden";
    this.setState({ showHideSidenav: css });
  };
  // $(".menu-toggle").click(function () {
  //   $(".nav").toggleClass("mobile-nav");
  //   $(this).toggleClass("is-active");
  // });

  const authLinks = (
    <ul>
      <li>
        <Link to="/profiles">Network Users</Link>
      </li>
      <li>
        <Link to="/posts">Posts</Link>
      </li>
      <li>
        <Link to="/dashboard">
          <i className="fas fa-user"></i>{" "}
          <span className="hide-sm">
            {user ? trimmedUserName(user.name) : "Profile"}
          </span>
          {/* <span className="hide-sm">{user ? user.name : "Dashboard"}</span> */}
        </Link>
      </li>
      <li>
        <a onClick={logout} href="#!">
          <i className="fas fa-sign-out-alt"></i>
          <span className="hide-sm">Logout</span>
        </a>
      </li>
    </ul>
  );

  const guestLinks = (
    <ul>
      <li>
        <Link to="/profiles">Network Users</Link>
      </li>
      <li>
        <Link to="/register">Register</Link>
      </li>
      <li>
        <Link to="/login">Login</Link>
      </li>
    </ul>
  );

  return (
    <div className="nav-alt-wrapper">
      <nav className="navbar-alt bg-dark">
        <h1>
          <Link to="/" id="nav-alt-logo">
            {/* <i className="fas fa-terminal"></i> */}
            <i className="fas fa-code-branch"></i> TechNetwork
          </Link>
        </h1>
        <div class="menu-toggle" id="mobile-menu" onClick={menuToggleFunc()}>
          <span class="bar"></span>
          <span class="bar"></span>
          <span class="bar"></span>
        </div>
        <ul class="nav-alt no-search">
          <li class="nav-alt-item">
            <a href="#">Home</a>
          </li>
          <li class="nav-alt-item">
            <a href="#">About</a>
          </li>
          <li class="nav-alt-item">
            <a href="#">Work</a>
          </li>
          <li class="nav-alt-item">
            <a href="#">Careers</a>
          </li>
          <li class="nav-alt-item">
            <a href="#">Contact Us</a>
          </li>
          <i class="fas fa-search" id="search-icon"></i>
          <input class="search-input" type="text" placeholder="Search.." />
        </ul>

        {/* {!loading && (
          <Fragment>{isAuthenticated ? authLinks : guestLinks} </Fragment>
        )} */}
      </nav>
    </div>
  );
};

NavbarAlternative.propTypes = {
  logout: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps, { logout })(NavbarAlternative);
